CREATE TABLE IF NOT EXISTS out_of_school_rate (
  Code VARCHAR(10) PRIMARY KEY,
  Countries VARCHAR(255),
  Region VARCHAR(255),
  Development_Regions VARCHAR(255),
  Total DECIMAL(15,9),
  Female DECIMAL(15,9),
  Male DECIMAL(15,9),
  Rural_Residence DECIMAL(15,9),
  Urban_Residence DECIMAL(15,9),
  Poorest_Wealth_quintile DECIMAL(15,9),
  Second_Wealth_quintile DECIMAL(15,9),
  Middle_Wealth_quintile DECIMAL(15,9),
  Fourth_Wealth_quintile DECIMAL(15,9),
  Richest_Wealth_quintile DECIMAL(15,9),
  Year INT
);

